import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import axios from "axios";

import PdfExtractor from "./PdfExtracter";
import EmailEditor from "./emailComponent/EmailEditor";
import ConfirmPage from "./ConfirmPage.jsx";
import SendEmailPage from "./SendEmailPage";

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<GmailAuth />} />
        <Route path="/auth/success" element={<AuthSuccess />} />
        {/* Page 1 → Upload Resume */}
        <Route path="/" element={<PdfExtractor />} />

        {/* Page 2 → Preview & Edit Email */}
        <Route path="/email" element={<EmailEditor />} />

        {/* Page 3 → Confirmed Email */}
        <Route path="/confirm" element={<ConfirmPage />} />

        {/* Page 3 → send Email */}
        <Route path="/send-email" element={<SendEmailPage />} />

      </Routes>
    </Router>
  );
}

export default App;
