import React, { useState, useRef } from "react";
import { Upload, FileText, Sparkles, ArrowRight, CheckCircle, X } from "lucide-react";

function PdfExtractor() {
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);
  const [dragActive, setDragActive] = useState(false);
  const [animateOut, setAnimateOut] = useState(false);
  const fileInputRef = useRef(null);

  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile && selectedFile.type === 'application/pdf') {
      setFile(selectedFile);
    } else {
      alert("Please select a PDF file only!");
    }
  };

  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") {
      setDragActive(true);
    } else if (e.type === "dragleave") {
      setDragActive(false);
    }
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);

    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      const droppedFile = e.dataTransfer.files[0];
      if (droppedFile.type === 'application/pdf') {
        setFile(droppedFile);
      } else {
        alert("Please drop a PDF file only!");
      }
    }
  };

  const handleUpload = async () => {
    if (!file) return alert("Please select a PDF file first!");

    const formData = new FormData();
    formData.append("file", file);

    try {
      setLoading(true);

      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 2000));

      setSuccess(true);

      // Trigger exit animation
      setTimeout(() => {
        setAnimateOut(true);
      }, 1000);

      // Navigate after animation
      setTimeout(() => {
        console.log("Navigating to email preview...");
        // navigate("/email");
      }, 2500);

    } catch (error) {
      console.error("Error:", error);
      alert("Upload failed!");
    } finally {
      setLoading(false);
    }
  };

  const removeFile = () => {
    setFile(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-purple-900 via-blue-900 to-indigo-900 flex items-center justify-center p-4 relative overflow-hidden">
      {/* Animated background elements */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute top-1/4 left-1/4 w-64 h-64 bg-purple-500 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse"></div>
        <div className="absolute top-1/3 right-1/4 w-80 h-80 bg-blue-500 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse delay-1000"></div>
        <div className="absolute bottom-1/4 left-1/3 w-72 h-72 bg-indigo-500 rounded-full mix-blend-multiply filter blur-xl opacity-20 animate-pulse delay-2000"></div>
      </div>

      {/* Floating particles */}
      <div className="absolute inset-0">
        {[...Array(20)].map((_, i) => (
          <div
            key={i}
            className="absolute w-1 h-1 bg-white rounded-full opacity-30 animate-pulse"
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              animationDelay: `${Math.random() * 3}s`,
              animationDuration: `${2 + Math.random() * 3}s`
            }}
          ></div>
        ))}
      </div>

      <div className={`relative z-10 transition-all duration-1000 ease-in-out transform ${
        animateOut 
          ? 'translate-y-full opacity-0 rotate-12 scale-75' 
          : success 
            ? 'scale-105' 
            : 'scale-100'
      }`}>
        <div className="backdrop-blur-lg bg-white/10 p-8 rounded-3xl shadow-2xl border border-white/20 max-w-md w-full mx-auto">
          {/* Header */}
          <div className="text-center mb-8">
            <div className="inline-flex items-center justify-center w-16 h-16 bg-gradient-to-r from-purple-500 to-blue-500 rounded-full mb-4 animate-bounce">
              <FileText className="w-8 h-8 text-white" />
            </div>
            <h2 className="text-3xl font-bold text-white mb-2 bg-gradient-to-r from-purple-300 to-blue-300 bg-clip-text text-transparent">
              Upload Resume PDF
            </h2>
            <p className="text-gray-300">
              Drag & drop your PDF or click to browse
            </p>
          </div>

          {/* Upload Area */}
          <div
            className={`relative border-2 border-dashed rounded-2xl p-8 text-center transition-all duration-300 cursor-pointer group ${
              dragActive
                ? 'border-purple-400 bg-purple-500/10 scale-105'
                : file
                ? 'border-green-400 bg-green-500/10'
                : 'border-gray-400 hover:border-purple-400 hover:bg-purple-500/5'
            }`}
            onDragEnter={handleDrag}
            onDragLeave={handleDrag}
            onDragOver={handleDrag}
            onDrop={handleDrop}
            onClick={() => !file && fileInputRef.current?.click()}
          >
            <input
              ref={fileInputRef}
              type="file"
              accept="application/pdf"
              onChange={handleFileChange}
              className="hidden"
            />

            {!file ? (
              <div className="space-y-4">
                <div className="flex justify-center">
                  <Upload className={`w-12 h-12 transition-all duration-300 ${
                    dragActive ? 'text-purple-400 scale-110' : 'text-gray-400 group-hover:text-purple-400'
                  }`} />
                </div>
                <div>
                  <p className="text-lg font-medium text-white mb-1">
                    {dragActive ? 'Drop it here!' : 'Choose PDF File'}
                  </p>
                  <p className="text-sm text-gray-400">
                    or drag and drop
                  </p>
                </div>
                <div className="flex items-center justify-center space-x-1">
                  <Sparkles className="w-4 h-4 text-purple-400 animate-pulse" />
                  <span className="text-xs text-gray-500">PDF files only</span>
                  <Sparkles className="w-4 h-4 text-purple-400 animate-pulse" />
                </div>
              </div>
            ) : (
              <div className="space-y-4 animate-fadeIn">
                <div className="flex items-center justify-between bg-white/10 rounded-lg p-3">
                  <div className="flex items-center space-x-3">
                    <FileText className="w-6 h-6 text-green-400" />
                    <div className="text-left">
                      <p className="text-white font-medium truncate max-w-48">
                        {file.name}
                      </p>
                      <p className="text-xs text-gray-400">
                        {formatFileSize(file.size)}
                      </p>
                    </div>
                  </div>
                  <button
                    onClick={(e) => {
                      e.stopPropagation();
                      removeFile();
                    }}
                    className="p-1 hover:bg-white/20 rounded-full transition-colors"
                  >
                    <X className="w-4 h-4 text-gray-400 hover:text-white" />
                  </button>
                </div>
                {success && (
                  <div className="flex items-center justify-center space-x-2 text-green-400 animate-bounce">
                    <CheckCircle className="w-5 h-5" />
                    <span className="text-sm font-medium">Upload Successful!</span>
                  </div>
                )}
              </div>
            )}
          </div>

          {/* Upload Button */}
          {file && !success && (
            <button
              onClick={handleUpload}
              disabled={loading}
              className="w-full mt-6 bg-gradient-to-r from-purple-600 to-blue-600 hover:from-purple-700 hover:to-blue-700 text-white font-bold py-4 px-6 rounded-2xl transition-all duration-300 transform hover:scale-105 hover:shadow-2xl disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none flex items-center justify-center space-x-2"
            >
              {loading ? (
                <>
                  <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                  <span>Processing...</span>
                </>
              ) : (
                <>
                  <span>Extract & Continue</span>
                  <ArrowRight className="w-5 h-5 group-hover:translate-x-1 transition-transform" />
                </>
              )}
            </button>
          )}

          {/* Loading Progress */}
          {loading && (
            <div className="mt-4">
              <div className="bg-gray-700 rounded-full h-2 overflow-hidden">
                <div className="bg-gradient-to-r from-purple-500 to-blue-500 h-full rounded-full animate-pulse"></div>
              </div>
              <p className="text-center text-sm text-gray-400 mt-2">
                Analyzing your resume...
              </p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default PdfExtractor;
