import React, { useState } from "react";
import { Send, Mail, Check, X, Users, Clock, CheckCircle2, XCircle, Loader2 } from "lucide-react";

function SendEmailPage() {
  // Mock data for demonstration since we don't have router
  const recipients = [
    { name: "John Doe", email: "john@example.com" },
    { name: "Jane Smith", email: "jane@example.com" },
    { name: "Mike Johnson", email: "mike@example.com" },
    { name: "Sarah Wilson", email: "sarah@example.com" },
    { name: "David Brown", email: "david@example.com" },
  ];

  const [currentEmail, setCurrentEmail] = useState(null);
  const [results, setResults] = useState([]);
  const [sending, setSending] = useState(false);

  const handleSendEmails = async () => {
    if (recipients.length === 0) return;
    setSending(true);
    setResults([]);

    // Simulate email sending process
    const mockResults = recipients.map(recipient => ({
      ...recipient,
      status: Math.random() > 0.2 ? "sent" : "failed" // 80% success rate
    }));

    let i = 0;
    const showNextEmail = () => {
      if (i >= mockResults.length) {
        setCurrentEmail(null);
        setSending(false);
        return;
      }

      const emailItem = mockResults[i];
      setCurrentEmail({ ...emailItem, status: "sending" });

      setTimeout(() => {
        setResults(prev => [...prev, emailItem]);
        setCurrentEmail(null);
        i++;
        setTimeout(showNextEmail, 300);
      }, 1500);
    };

    showNextEmail();
  };

  const getStats = () => {
    const sent = results.filter(r => r.status === "sent").length;
    const failed = results.filter(r => r.status === "failed").length;
    return { sent, failed, total: results.length };
  };

  const stats = getStats();

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 p-6">
      <div className="max-w-4xl mx-auto">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="inline-flex items-center justify-center w-16 h-16 bg-gradient-to-r from-purple-500 to-pink-500 rounded-full mb-4 shadow-lg">
            <Mail className="w-8 h-8 text-white" />
          </div>
          <h1 className="text-4xl font-bold text-white mb-2 bg-gradient-to-r from-purple-400 to-pink-400 bg-clip-text text-transparent">
            Email Campaign Manager
          </h1>
          <p className="text-slate-300 text-lg">Send personalized emails to your recipients</p>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-8">
          <div className="bg-white/10 backdrop-blur-lg rounded-xl p-4 border border-white/20">
            <div className="flex items-center">
              <Users className="w-6 h-6 text-blue-400 mr-2" />
              <div>
                <p className="text-slate-300 text-sm">Total Recipients</p>
                <p className="text-2xl font-bold text-white">{recipients.length}</p>
              </div>
            </div>
          </div>

          <div className="bg-white/10 backdrop-blur-lg rounded-xl p-4 border border-white/20">
            <div className="flex items-center">
              <CheckCircle2 className="w-6 h-6 text-green-400 mr-2" />
              <div>
                <p className="text-slate-300 text-sm">Sent</p>
                <p className="text-2xl font-bold text-green-400">{stats.sent}</p>
              </div>
            </div>
          </div>

          <div className="bg-white/10 backdrop-blur-lg rounded-xl p-4 border border-white/20">
            <div className="flex items-center">
              <XCircle className="w-6 h-6 text-red-400 mr-2" />
              <div>
                <p className="text-slate-300 text-sm">Failed</p>
                <p className="text-2xl font-bold text-red-400">{stats.failed}</p>
              </div>
            </div>
          </div>

          <div className="bg-white/10 backdrop-blur-lg rounded-xl p-4 border border-white/20">
            <div className="flex items-center">
              <Clock className="w-6 h-6 text-yellow-400 mr-2" />
              <div>
                <p className="text-slate-300 text-sm">Progress</p>
                <p className="text-2xl font-bold text-white">{stats.total}/{recipients.length}</p>
              </div>
            </div>
          </div>
        </div>

        {/* Send Button */}
        <div className="text-center mb-8">
          <button
            onClick={handleSendEmails}
            disabled={sending}
            className="inline-flex items-center px-8 py-4 bg-gradient-to-r from-purple-500 to-pink-500 text-white font-bold rounded-xl shadow-lg hover:shadow-xl transform hover:scale-105 transition-all duration-300 disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none"
          >
            {sending ? (
              <Loader2 className="w-6 h-6 mr-2 animate-spin" />
            ) : (
              <Send className="w-6 h-6 mr-2" />
            )}
            {sending ? "Sending Emails..." : "Start Email Campaign"}
          </button>
        </div>

        {/* Progress Bar */}
        {sending && (
          <div className="mb-6">
            <div className="bg-white/10 rounded-full h-3 backdrop-blur-lg border border-white/20">
              <div 
                className="bg-gradient-to-r from-purple-500 to-pink-500 h-3 rounded-full transition-all duration-500 shadow-lg"
                style={{ width: `${(stats.total / recipients.length) * 100}%` }}
              ></div>
            </div>
            <p className="text-center text-slate-300 mt-2">
              {stats.total} of {recipients.length} emails processed
            </p>
          </div>
        )}

        {/* Current Sending */}
        {currentEmail && (
          <div className="mb-6 transform animate-fade-in">
            <div className="bg-gradient-to-r from-blue-500/20 to-purple-500/20 backdrop-blur-lg rounded-xl p-6 border border-blue-500/30 shadow-xl">
              <div className="flex items-center">
                <div className="relative mr-4">
                  <div className="w-12 h-12 bg-gradient-to-r from-blue-500 to-purple-500 rounded-full flex items-center justify-center">
                    <Loader2 className="w-6 h-6 text-white animate-spin" />
                  </div>
                  <div className="absolute -inset-1 bg-gradient-to-r from-blue-500 to-purple-500 rounded-full blur opacity-30 animate-pulse"></div>
                </div>
                <div className="flex-1">
                  <h3 className="text-white font-semibold text-lg">Sending...</h3>
                  <p className="text-blue-300 font-medium">{currentEmail.name}</p>
                  <p className="text-slate-300 text-sm">{currentEmail.email}</p>
                </div>
                <div className="flex items-center space-x-2">
                  <div className="w-2 h-2 bg-blue-400 rounded-full animate-ping"></div>
                  <div className="w-2 h-2 bg-blue-400 rounded-full animate-ping" style={{ animationDelay: '0.2s' }}></div>
                  <div className="w-2 h-2 bg-blue-400 rounded-full animate-ping" style={{ animationDelay: '0.4s' }}></div>
                </div>
              </div>
            </div>
          </div>
        )}

        {/* Results */}
        <div className="space-y-3">
          {results.map((result, index) => (
            <div
              key={index}
              className={`transform transition-all duration-500 animate-slide-up bg-white/10 backdrop-blur-lg rounded-xl p-4 border ${
                result.status === "sent" 
                  ? "border-green-500/50 bg-gradient-to-r from-green-500/10 to-emerald-500/10" 
                  : "border-red-500/50 bg-gradient-to-r from-red-500/10 to-pink-500/10"
              } shadow-lg hover:shadow-xl hover:transform hover:scale-[1.02]`}
              style={{ animationDelay: `${index * 0.1}s` }}
            >
              <div className="flex items-center justify-between">
                <div className="flex items-center">
                  <div className={`w-10 h-10 rounded-full flex items-center justify-center mr-4 ${
                    result.status === "sent" 
                      ? "bg-green-500/20 border-2 border-green-500/50" 
                      : "bg-red-500/20 border-2 border-red-500/50"
                  }`}>
                    {result.status === "sent" ? (
                      <CheckCircle2 className="w-5 h-5 text-green-400" />
                    ) : (
                      <XCircle className="w-5 h-5 text-red-400" />
                    )}
                  </div>
                  <div>
                    <h4 className="text-white font-semibold">{result.name}</h4>
                    <p className="text-slate-300 text-sm">{result.email}</p>
                  </div>
                </div>
                <div className="flex items-center">
                  <span className={`px-3 py-1 rounded-full text-sm font-medium ${
                    result.status === "sent" 
                      ? "bg-green-500/20 text-green-300 border border-green-500/30" 
                      : "bg-red-500/20 text-red-300 border border-red-500/30"
                  }`}>
                    {result.status.toUpperCase()}
                  </span>
                  {result.status === "sent" && (
                    <div className="ml-3 text-green-400 animate-bounce">
                      <Check className="w-5 h-5" />
                    </div>
                  )}
                  {result.status === "failed" && (
                    <div className="ml-3 text-red-400 animate-pulse">
                      <X className="w-5 h-5" />
                    </div>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Completion Message */}
        {!sending && results.length > 0 && results.length === recipients.length && (
          <div className="mt-8 text-center animate-fade-in">
            <div className="bg-gradient-to-r from-green-500/20 to-emerald-500/20 backdrop-blur-lg rounded-xl p-6 border border-green-500/30">
              <CheckCircle2 className="w-12 h-12 text-green-400 mx-auto mb-3" />
              <h3 className="text-2xl font-bold text-white mb-2">Campaign Complete!</h3>
              <p className="text-slate-300">
                Successfully sent {stats.sent} emails, {stats.failed} failed
              </p>
            </div>
          </div>
        )}
      </div>

      <style jsx>{`
        @keyframes fade-in {
          from { opacity: 0; transform: translateY(10px); }
          to { opacity: 1; transform: translateY(0); }
        }

        @keyframes slide-up {
          from { opacity: 0; transform: translateY(20px); }
          to { opacity: 1; transform: translateY(0); }
        }

        .animate-fade-in {
          animation: fade-in 0.5s ease-out;
        }

        .animate-slide-up {
          animation: slide-up 0.6s ease-out;
        }
      `}</style>
    </div>
  );
}

export default SendEmailPage;
