// src/components/EmailEditor/EmailEditor.jsx
import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { Mail } from "lucide-react";
import "./EmailEditor.css";

export default function EmailEditor() {
  const [emailDraft, setEmailDraft] = useState("Loading email...");
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchEmail = async () => {
      try {
        const response = await axios.post("http://localhost:8080/resume/generate-mail");
        setEmailDraft(response.data);
      } catch (error) {
        console.error("Error fetching email:", error);
        setEmailDraft("Error fetching email from backend.");
      } finally {
        setLoading(false);
      }
    };
    fetchEmail();
  }, []);

  const handleConfirm = async () => {
  try {
    await axios.post("http://localhost:8080/resume/update-mail", emailDraft, {
      headers: { "Content-Type": "text/plain" }, // plain string, not JSON object
    });
    navigate("/confirm", { state: { finalEmail: emailDraft } });
  } catch (error) {
    console.error("Error updating email:", error);
    alert("Failed to update email in backend!");
  }
};


  return (
    <div className="email-editor-container">
      {/* Floating Mail Icon */}
      <div className="mail-icon-wrapper">
        <Mail className="mail-icon" />
      </div>

      {/* Card */}
      <div className="email-card">
        <h2 className="email-title">Email Preview & Editor</h2>
        <p className="email-subtitle">
          Review and customize your AI-generated email
        </p>

        {loading ? (
          <p className="loading-text">Generating email...</p>
        ) : (
          <>
            <div className="textarea-wrapper">
              <textarea
                value={emailDraft}
                onChange={(e) => setEmailDraft(e.target.value)}
                className="email-textarea"
              />
            </div>
            <button onClick={handleConfirm} className="confirm-btn">
              Confirm & Send â†’
            </button>
          </>
        )}
      </div>
    </div>
  );
}
