import React, { useState } from 'react';
import { Mail, Send, Bot, Zap, Users, ArrowRight, Eye, EyeOff, Lock, User } from 'lucide-react';
import { useNavigate } from "react-router-dom";

const LoginPage = () => {
  const [isLoading, setIsLoading] = useState(false);
  const [hoveredButton, setHoveredButton] = useState(null);
  const [isSignUp, setIsSignUp] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmPassword: '',
    fullName: ''
  });
  const [errors, setErrors] = useState({});

  const handleSocialLogin = (provider) => {
    setIsLoading(true);
    setTimeout(() => {
      setIsLoading(false);
      console.log(`Authenticating with ${provider}...`);
    }, 2000);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.email) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email is invalid';
    }

    if (!formData.password) {
      newErrors.password = 'Password is required';
    } else if (formData.password.length < 6) {
      newErrors.password = 'Password must be at least 6 characters';
    }

    if (isSignUp) {
      if (!formData.fullName) {
        newErrors.fullName = 'Full name is required';
      }

      if (!formData.confirmPassword) {
        newErrors.confirmPassword = 'Please confirm your password';
      } else if (formData.password !== formData.confirmPassword) {
        newErrors.confirmPassword = 'Passwords do not match';
      }
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

const navigate = useNavigate();


 const handleSubmit = (e) => {
  e.preventDefault();

  if (!validateForm()) return;

  setIsLoading(true);
  setTimeout(() => {
    setIsLoading(false);
    console.log(`${isSignUp ? 'Signing up' : 'Signing in'} with:`, formData);


      // Redirect to PdfExtractor after successful signup
      navigate("/extract");

      // Redirect somewhere else after sign in, e.g. dashboard


  }, 2000);
};



  const toggleAuthMode = () => {
    setIsSignUp(!isSignUp);
    setFormData({
      email: '',
      password: '',
      confirmPassword: '',
      fullName: ''
    });
    setErrors({});
    setShowPassword(false);
    setShowConfirmPassword(false);
  };

  return (
    <div className="login-container">
      <style jsx>{`
        .login-container {
          min-height: 100vh;
          background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
          display: flex;
          align-items: center;
          justify-content: center;
          font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
          overflow: hidden;
          position: relative;
          padding: 2rem 1rem;
        }

        .background-animation {
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          overflow: hidden;
          z-index: 1;
        }

        .floating-icon {
          position: absolute;
          opacity: 0.1;
          animation: float 20s infinite linear;
          color: white;
        }

        .floating-icon:nth-child(1) {
          top: 10%;
          left: 10%;
          animation-delay: 0s;
        }

        .floating-icon:nth-child(2) {
          top: 20%;
          right: 15%;
          animation-delay: -5s;
        }

        .floating-icon:nth-child(3) {
          bottom: 30%;
          left: 20%;
          animation-delay: -10s;
        }

        .floating-icon:nth-child(4) {
          bottom: 20%;
          right: 10%;
          animation-delay: -15s;
        }

        @keyframes float {
          0% {
            transform: translateY(0px) rotate(0deg);
          }
          25% {
            transform: translateY(-20px) rotate(90deg);
          }
          50% {
            transform: translateY(-10px) rotate(180deg);
          }
          75% {
            transform: translateY(-30px) rotate(270deg);
          }
          100% {
            transform: translateY(0px) rotate(360deg);
          }
        }

        .login-card {
          background: rgba(255, 255, 255, 0.95);
          backdrop-filter: blur(20px);
          border-radius: 24px;
          padding: 3rem;
          box-shadow: 0 25px 50px rgba(0, 0, 0, 0.2);
          width: 100%;
          max-width: 440px;
          position: relative;
          z-index: 2;
          border: 1px solid rgba(255, 255, 255, 0.2);
          animation: cardSlideIn 0.8s ease-out;
          max-height: 79vh;
          overflow-y: auto;
        }

        @keyframes cardSlideIn {
          0% {
            opacity: 0;
            transform: translateY(30px);
          }
          100% {
            opacity: 1;
            transform: translateY(0);
          }
        }

        .header-section {
          text-align: center;
          margin-bottom: 2rem;
        }

        .logo-container {
          display: inline-flex;
          align-items: center;
          justify-content: center;
          width: 70px;
          height: 70px;
          background: linear-gradient(135deg, #667eea, #764ba2);
          border-radius: 18px;
          margin-bottom: 1rem;
          animation: logoGlow 2s ease-in-out infinite alternate;
        }

        @keyframes logoGlow {
          0% {
            box-shadow: 0 0 20px rgba(102, 126, 234, 0.3);
          }
          100% {
            box-shadow: 0 0 30px rgba(102, 126, 234, 0.6);
          }
        }

        .logo-icon {
          color: white;
          width: 28px;
          height: 28px;
        }

        .app-title {
          font-size: 1.8rem;
          font-weight: 700;
          color: #2d3748;
          margin-bottom: 0.3rem;
          background: linear-gradient(135deg, #667eea, #764ba2);
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
          background-clip: text;
        }

        .app-subtitle {
          font-size: 0.9rem;
          color: #718096;
          line-height: 1.4;
          margin-bottom: 0.5rem;
        }

        .auth-toggle {
          font-size: 1.2rem;
          font-weight: 600;
          color: #4a5568;
          margin-bottom: 1.5rem;
        }

        .features-preview {
          display: flex;
          justify-content: center;
          gap: 1.5rem;
          margin: 1.5rem 0;
          padding: 1rem;
          background: linear-gradient(135deg, rgba(102, 126, 234, 0.1), rgba(118, 75, 162, 0.1));
          border-radius: 12px;
          border: 1px solid rgba(102, 126, 234, 0.2);
        }

        .feature-item {
          display: flex;
          flex-direction: column;
          align-items: center;
          gap: 0.3rem;
        }

        .feature-icon {
          width: 20px;
          height: 20px;
          color: #667eea;
        }

        .feature-text {
          font-size: 0.7rem;
          color: #4a5568;
          font-weight: 500;
        }

        .login-form {
          margin-bottom: 2rem;
        }

        .form-group {
          margin-bottom: 1.5rem;
        }

        .form-label {
          display: block;
          font-size: 0.9rem;
          font-weight: 600;
          color: #4a5568;
          margin-bottom: 0.5rem;
        }

        .input-wrapper {
          position: relative;
        }

        .form-input {
          width: 100%;
          padding: 1rem;
          padding-left: 3rem;
          border: 2px solid #e2e8f0;
          border-radius: 12px;
          font-size: 1rem;
          transition: all 0.3s ease;
          background: rgba(255, 255, 255, 0.8);
          box-sizing: border-box;
        }

        .form-input:focus {
          outline: none;
          border-color: #667eea;
          box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.1);
          background: white;
        }

        .form-input.error {
          border-color: #e53e3e;
        }

        .input-icon {
          position: absolute;
          left: 12px;
          top: 50%;
          transform: translateY(-50%);
          width: 18px;
          height: 18px;
          color: #a0aec0;
        }

        .password-toggle {
          position: absolute;
          right: 12px;
          top: 50%;
          transform: translateY(-50%);
          cursor: pointer;
          color: #a0aec0;
          transition: color 0.3s ease;
        }

        .password-toggle:hover {
          color: #667eea;
        }

        .error-message {
          color: #e53e3e;
          font-size: 0.8rem;
          margin-top: 0.3rem;
        }

        .submit-btn {
          width: 100%;
          padding: 1rem;
          background: linear-gradient(135deg, #667eea, #764ba2);
          color: white;
          border: none;
          border-radius: 12px;
          font-size: 1rem;
          font-weight: 600;
          cursor: pointer;
          transition: all 0.3s ease;
          display: flex;
          align-items: center;
          justify-content: center;
          gap: 0.5rem;
          margin-bottom: 1rem;
        }

        .submit-btn:hover:not(:disabled) {
          transform: translateY(-2px);
          box-shadow: 0 6px 25px rgba(102, 126, 234, 0.4);
        }

        .submit-btn:disabled {
          opacity: 0.7;
          cursor: not-allowed;
        }

        .auth-switch {
          text-align: center;
          margin-bottom: 2rem;
        }

        .auth-switch-text {
          color: #718096;
          font-size: 0.9rem;
        }

        .auth-switch-link {
          color: #667eea;
          font-weight: 600;
          cursor: pointer;
          text-decoration: none;
          margin-left: 0.3rem;
          transition: color 0.3s ease;
        }

        .auth-switch-link:hover {
          color: #764ba2;
          text-decoration: underline;
        }

        .divider {
          margin: 2rem 0;
          text-align: center;
          position: relative;
          color: #a0aec0;
          font-size: 0.9rem;
        }

        .divider:before {
          content: '';
          position: absolute;
          top: 50%;
          left: 0;
          right: 0;
          height: 1px;
          background: linear-gradient(90deg, transparent, #e2e8f0, transparent);
          z-index: 0;
        }

        .divider span {
          background: white;
          padding: 0 1rem;
          position: relative;
          z-index: 1;
        }

        .social-buttons {
          display: flex;
          flex-direction: column;
          gap: 0.8rem;
        }

        .social-button {
          display: flex;
          align-items: center;
          justify-content: center;
          gap: 0.8rem;
          padding: 0.8rem 1.2rem;
          border: 2px solid transparent;
          border-radius: 12px;
          font-size: 0.9rem;
          font-weight: 600;
          cursor: pointer;
          transition: all 0.3s ease;
          position: relative;
          overflow: hidden;
          text-decoration: none;
        }

        .social-button:before {
          content: '';
          position: absolute;
          top: 0;
          left: -100%;
          width: 100%;
          height: 100%;
          background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.4), transparent);
          transition: left 0.5s;
        }

        .social-button:hover:before {
          left: 100%;
        }

        .google-btn {
          background: linear-gradient(135deg, #ea4335, #fbbc04);
          color: white;
          box-shadow: 0 3px 12px rgba(234, 67, 53, 0.3);
        }

        .google-btn:hover {
          transform: translateY(-1px);
          box-shadow: 0 5px 20px rgba(234, 67, 53, 0.4);
        }

        .linkedin-btn {
          background: linear-gradient(135deg, #0077b5, #00a0dc);
          color: white;
          box-shadow: 0 3px 12px rgba(0, 119, 181, 0.3);
        }

        .linkedin-btn:hover {
          transform: translateY(-1px);
          box-shadow: 0 5px 20px rgba(0, 119, 181, 0.4);
        }

        .github-btn {
          background: linear-gradient(135deg, #333, #24292e);
          color: white;
          box-shadow: 0 3px 12px rgba(51, 51, 51, 0.3);
        }

        .github-btn:hover {
          transform: translateY(-1px);
          box-shadow: 0 5px 20px rgba(51, 51, 51, 0.4);
        }

        .social-icon {
          width: 18px;
          height: 18px;
        }

        .loading-spinner {
          width: 20px;
          height: 20px;
          border: 2px solid rgba(255, 255, 255, 0.3);
          border-radius: 50%;
          border-top-color: white;
          animation: spin 1s ease-in-out infinite;
        }

        @keyframes spin {
          to {
            transform: rotate(360deg);
          }
        }

        .security-note {
          text-align: center;
          font-size: 0.75rem;
          color: #718096;
          margin-top: 1.5rem;
          padding: 0.8rem;
          background: rgba(102, 126, 234, 0.05);
          border-radius: 10px;
          border: 1px solid rgba(102, 126, 234, 0.1);
        }

        @media (max-width: 480px) {
          .login-card {
            margin: 0.5rem;
            padding: 2rem 1.5rem;
            max-height: 95vh;
          }

          .features-preview {
            flex-direction: column;
            gap: 0.8rem;
            margin: 1rem 0;
          }

          .app-title {
            font-size: 1.5rem;
          }

          .form-input {
            padding: 0.8rem;
            padding-left: 2.5rem;
          }
        }
      `}</style>

      {/* Floating Background Animation */}
      <div className="background-animation">
        <div className="floating-icon">
          <Mail size={40} />
        </div>
        <div className="floating-icon">
          <Bot size={35} />
        </div>
        <div className="floating-icon">
          <Send size={30} />
        </div>
        <div className="floating-icon">
          <Zap size={45} />
        </div>
      </div>

      <div className="login-card">
        {/* Header Section */}
        <div className="header-section">
          <div className="logo-container">
            <Mail className="logo-icon" />
          </div>
          <h1 className="app-title">MailBot Pro</h1>
          <p className="app-subtitle">
            AI-Powered Bulk Email Automation Platform
          </p>
          <div className="auth-toggle">
            {isSignUp ? 'Create Account' : 'Welcome Back'}
          </div>
        </div>

        {/* Features Preview */}
        <div className="features-preview">
          <div className="feature-item">
            <Bot className="feature-icon" />
            <span className="feature-text">AI Generated</span>
          </div>
          <div className="feature-item">
            <Users className="feature-icon" />
            <span className="feature-text">Bulk Sending</span>
          </div>
          <div className="feature-item">
            <Zap className="feature-icon" />
            <span className="feature-text">Automation</span>
          </div>
        </div>

        {/* Login/Signup Form */}
        <form className="login-form" onSubmit={handleSubmit}>
          {isSignUp && (
            <div className="form-group">
              <label className="form-label">Full Name</label>
              <div className="input-wrapper">
                <User className="input-icon" />
                <input
                  type="text"
                  name="fullName"
                  className={`form-input ${errors.fullName ? 'error' : ''}`}
                  placeholder="Enter your full name"
                  value={formData.fullName}
                  onChange={handleInputChange}
                />
              </div>
              {errors.fullName && <div className="error-message">{errors.fullName}</div>}
            </div>
          )}

          <div className="form-group">
            <label className="form-label">Email Address</label>
            <div className="input-wrapper">
              <Mail className="input-icon" />
              <input
                type="email"
                name="email"
                className={`form-input ${errors.email ? 'error' : ''}`}
                placeholder="Enter your email"
                value={formData.email}
                onChange={handleInputChange}
              />
            </div>
            {errors.email && <div className="error-message">{errors.email}</div>}
          </div>

          <div className="form-group">
            <label className="form-label">Password</label>
            <div className="input-wrapper">
              <Lock className="input-icon" />
              <input
                type={showPassword ? "text" : "password"}
                name="password"
                className={`form-input ${errors.password ? 'error' : ''}`}
                placeholder="Enter your password"
                value={formData.password}
                onChange={handleInputChange}
              />
              <div className="password-toggle" onClick={() => setShowPassword(!showPassword)}>
                {showPassword ? <EyeOff size={18} /> : <Eye size={18} />}
              </div>
            </div>
            {errors.password && <div className="error-message">{errors.password}</div>}
          </div>

          {isSignUp && (
            <div className="form-group">
              <label className="form-label">Confirm Password</label>
              <div className="input-wrapper">
                <Lock className="input-icon" />
                <input
                  type={showConfirmPassword ? "text" : "password"}
                  name="confirmPassword"
                  className={`form-input ${errors.confirmPassword ? 'error' : ''}`}
                  placeholder="Confirm your password"
                  value={formData.confirmPassword}
                  onChange={handleInputChange}
                />
                <div className="password-toggle" onClick={() => setShowConfirmPassword(!showConfirmPassword)}>
                  {showConfirmPassword ? <EyeOff size={18} /> : <Eye size={18} />}
                </div>
              </div>
              {errors.confirmPassword && <div className="error-message">{errors.confirmPassword}</div>}
            </div>
          )}

          <button
            type="submit"
            className="submit-btn"
            disabled={isLoading}
          >
            {isLoading ? (
              <div className="loading-spinner"></div>
            ) : (
              <>
                {isSignUp ? 'Create Account' : 'Sign In'}
                <ArrowRight size={18} />
              </>
            )}
          </button>
        </form>

        {/* Auth Mode Toggle */}
        <div className="auth-switch">
          <span className="auth-switch-text">
            {isSignUp ? 'Already have an account?' : "Don't have an account?"}
          </span>
          <span className="auth-switch-link" onClick={toggleAuthMode}>
            {isSignUp ? 'Sign In' : 'Sign Up'}
          </span>
        </div>

        {/* Divider */}
        <div className="divider">
          <span>Or continue with</span>
        </div>

        {/* Social Login Buttons */}
        <div className="social-buttons">
          <button
            className="social-button google-btn"
            onClick={() => handleSocialLogin('Google')}
            onMouseEnter={() => setHoveredButton('google')}
            onMouseLeave={() => setHoveredButton(null)}
            disabled={isLoading}
          >
            {isLoading && hoveredButton === 'google' ? (
              <div className="loading-spinner"></div>
            ) : (
              <svg className="social-icon" viewBox="0 0 24 24">
                <path fill="currentColor" d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"/>
                <path fill="currentColor" d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z"/>
                <path fill="currentColor" d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z"/>
                <path fill="currentColor" d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z"/>
              </svg>
            )}
            Continue with Gmail
          </button>

          <button
            className="social-button linkedin-btn"
            onClick={() => handleSocialLogin('LinkedIn')}
            onMouseEnter={() => setHoveredButton('linkedin')}
            onMouseLeave={() => setHoveredButton(null)}
            disabled={isLoading}
          >
            {isLoading && hoveredButton === 'linkedin' ? (
              <div className="loading-spinner"></div>
            ) : (
              <svg className="social-icon" viewBox="0 0 24 24">
                <path fill="currentColor" d="M20.447 20.452h-3.554v-5.569c0-1.328-.027-3.037-1.852-3.037-1.853 0-2.136 1.445-2.136 2.939v5.667H9.351V9h3.414v1.561h.046c.477-.9 1.637-1.85 3.37-1.85 3.601 0 4.267 2.37 4.267 5.455v6.286zM5.337 7.433c-1.144 0-2.063-.926-2.063-2.065 0-1.138.92-2.063 2.063-2.063 1.14 0 2.064.925 2.064 2.063 0 1.139-.925 2.065-2.064 2.065zm1.782 13.019H3.555V9h3.564v11.452zM22.225 0H1.771C.792 0 0 .774 0 1.729v20.542C0 23.227.792 24 1.771 24h20.451C23.2 24 24 23.227 24 22.271V1.729C24 .774 23.2 0 22.222 0h.003z"/>
              </svg>
            )}
            Continue with LinkedIn
          </button>

          <button
            className="social-button github-btn"
            onClick={() => handleSocialLogin('GitHub')}
            onMouseEnter={() => setHoveredButton('github')}
            onMouseLeave={() => setHoveredButton(null)}
            disabled={isLoading}
          >
            {isLoading && hoveredButton === 'github' ? (
              <div className="loading-spinner"></div>
            ) : (
              <svg className="social-icon" viewBox="0 0 24 24">
                <path fill="currentColor" d="M12 0c-6.626 0-12 5.373-12 12 0 5.302 3.438 9.8 8.207 11.387.599.111.793-.261.793-.577v-2.234c-3.338.726-4.033-1.416-4.033-1.416-.546-1.387-1.333-1.756-1.333-1.756-1.089-.745.083-.729.083-.729 1.205.084 1.839 1.237 1.839 1.237 1.07 1.834 2.807 1.304 3.492.997.107-.775.418-1.305.762-1.604-2.665-.305-5.467-1.334-5.467-5.931 0-1.311.469-2.381 1.236-3.221-.124-.303-.535-1.524.117-3.176 0 0 1.008-.322 3.301 1.23.957-.266 1.983-.399 3.003-.404 1.02.005 2.047.138 3.006.404 2.291-1.552 3.297-1.23 3.297-1.23.653 1.653.242 2.874.118 3.176.77.84 1.235 1.911 1.235 3.221 0 4.609-2.807 5.624-5.479 5.921.43.372.823 1.102.823 2.222v3.293c0 .319.192.694.801.576 4.765-1.589 8.199-6.086 8.199-11.386 0-6.627-5.373-12-12-12z"/>
              </svg>
            )}
            Continue with GitHub
          </button>
        </div>

        {/* Security Note */}
        <div className="security-note">
          <Zap size={14} style={{display: 'inline', marginRight: '6px', verticalAlign: 'text-bottom'}} />
          Secure authentication • Your data is protected
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
