import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function EmailEditor() {
  const [emailDraft, setEmailDraft] = useState("Loading email...");
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // Fetch AI-generated email when page loads
  useEffect(() => {
    const fetchEmail = async () => {
      try {
        const response = await axios.post("http://localhost:8080/resume/generate-mail");
        setEmailDraft(response.data);
      } catch (error) {
        console.error("Error fetching email:", error);
        setEmailDraft("Error fetching email from backend.");
      } finally {
        setLoading(false);
      }
    };
    fetchEmail();
  }, []);

  const handleConfirm = () => {
    navigate("/confirm", { state: { finalEmail: emailDraft } });
  };

  return (
    <div>
      <h2>Email Preview & Edit</h2>
      {loading ? (
        <p>Generating email...</p>
      ) : (
        <>
          <textarea
            rows="10"
            cols="60"
            value={emailDraft}
            onChange={(e) => setEmailDraft(e.target.value)}
          />
          <br />
          <button onClick={handleConfirm}>Confirm</button>
        </>
      )}
    </div>
  );
}

export default EmailEditor;
