// src/components/PdfExtractor/PdfExtractor.jsx
import React, { useState, useRef } from "react";
import { Upload, FileText, ArrowRight, CheckCircle, X, Sparkles } from "lucide-react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { formatFileSize } from "./PdfUtils";
import "./PdfExtracter.css";

export default function PdfExtractor() {
  const [file, setFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);
  const [dragActive, setDragActive] = useState(false);
  const [animateOut, setAnimateOut] = useState(false);
  const fileInputRef = useRef(null);
  const navigate = useNavigate();

  // File handlers
  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    if (selectedFile && selectedFile.type === "application/pdf") setFile(selectedFile);
    else alert("Please select a PDF file only!");
  };

  const handleDrag = (e) => {
    e.preventDefault();
    e.stopPropagation();
    if (e.type === "dragenter" || e.type === "dragover") setDragActive(true);
    if (e.type === "dragleave") setDragActive(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    e.stopPropagation();
    setDragActive(false);
    if (e.dataTransfer.files && e.dataTransfer.files[0]) {
      const droppedFile = e.dataTransfer.files[0];
      if (droppedFile.type === "application/pdf") setFile(droppedFile);
      else alert("Please drop a PDF file only!");
    }
  };

  const removeFile = () => {
    setFile(null);
    if (fileInputRef.current) fileInputRef.current.value = "";
  };

  // Upload handler
  const handleUpload = async () => {
    if (!file) return alert("Please select a PDF file first!");

    const formData = new FormData();
    formData.append("file", file);

    try {
      setLoading(true);

      // Upload to backend
      await axios.post("http://localhost:8080/resume/upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      setSuccess(true);
      setTimeout(() => setAnimateOut(true), 1000);

      // Navigate after animation
      setTimeout(() => {
        navigate("/email");
      }, 1500);

    } catch (err) {
      console.error(err);
      alert("Upload failed!");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="pdf-extractor-container">
      <div className={`pdf-card ${animateOut ? "animate-out" : ""}`}>
        {/* Header */}
        <div className="text-center mb-6">
          <div className="inline-flex items-center justify-center w-16 h-16 bg-gradient-to-r from-purple-500 to-blue-500 rounded-full mb-4 animate-bounce">
            <FileText className="w-8 h-8 text-white" />
          </div>
          <h2 className="text-white text-2xl font-bold">Upload Resume PDF</h2>
          <p className="text-gray-300">Drag & drop your PDF or click to browse</p>
        </div>

        {/* Upload area */}
        <div
          className={`pdf-upload-area ${dragActive ? "drag-active" : file ? "file-selected" : ""}`}
          onDragEnter={handleDrag}
          onDragLeave={handleDrag}
          onDragOver={handleDrag}
          onDrop={handleDrop}
          onClick={() => !file && fileInputRef.current?.click()}
        >
          <input
            ref={fileInputRef}
            type="file"
            accept="application/pdf"
            onChange={handleFileChange}
            className="hidden"
          />

          {!file ? (
            <div className="space-y-4">
              <Upload className="w-12 h-12 text-gray-400" />
              <p className="text-white">Choose PDF File or drag & drop</p>
              <div className="flex items-center justify-center space-x-1">
                <Sparkles className="w-4 h-4 text-purple-400 animate-pulse" />
                <span className="text-xs text-gray-500">PDF files only</span>
                <Sparkles className="w-4 h-4 text-purple-400 animate-pulse" />
              </div>
            </div>
          ) : (
            <div className="space-y-4">
              <div className="flex items-center justify-between bg-white/10 rounded-lg p-3">
                <div className="flex items-center space-x-3">
                  <FileText className="w-6 h-6 text-green-400" />
                  <div>
                    <p className="text-white truncate max-w-xs">{file.name}</p>
                    <p className="text-xs text-gray-400">{formatFileSize(file.size)}</p>
                  </div>
                </div>
                <button onClick={removeFile} className="p-1 hover:bg-white/20 rounded-full">
                  <X className="w-4 h-4 text-gray-400 hover:text-white" />
                </button>
              </div>
              {success && (
                <div className="flex items-center justify-center space-x-2 text-green-400 animate-bounce">
                  <CheckCircle className="w-5 h-5" />
                  <span className="text-sm font-medium">Upload Successful!</span>
                </div>
              )}
            </div>
          )}
        </div>

        {/* Upload button */}
        {file && !success && (
          <button onClick={handleUpload} disabled={loading} className="pdf-upload-btn">
            {loading ? (
              <>
                <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin"></div>
                <span>Uploading...</span>
              </>
            ) : (
              <>
                <span>Next â†’</span>
                <ArrowRight className="w-5 h-5" />
              </>
            )}
          </button>
        )}

        {/* Loading bar */}
        {loading && (
          <div className="pdf-loading-bar">
            <div className="pdf-loading-progress"></div>
          </div>
        )}
      </div>
    </div>
  );
}
