import React, { useState, useEffect } from "react";
import { Mail, Edit3, Send, Sparkles, ArrowRight } from "lucide-react";

function EmailEditor() {
  const [emailDraft, setEmailDraft] = useState("Loading email...");
  const [loading, setLoading] = useState(true);
  const [isEditing, setIsEditing] = useState(false);

  // Simulate API call since we can't make real HTTP requests
  useEffect(() => {
    const fetchEmail = async () => {
      // Simulate loading delay
      await new Promise(resolve => setTimeout(resolve, 2000));

      // Mock email content
      const mockEmail = `Subject: Application for Software Developer Position

Dear Hiring Manager,

I hope this email finds you well. I am writing to express my strong interest in the Software Developer position at your esteemed organization.

With my background in React, Node.js, and modern web technologies, I am confident that I can contribute effectively to your development team. My passion for creating innovative solutions and commitment to writing clean, maintainable code aligns perfectly with your company's values.

I have attached my resume for your review and would welcome the opportunity to discuss how my skills and experience can benefit your team.

Thank you for considering my application. I look forward to hearing from you soon.

Best regards,
[Your Name]`;

      setEmailDraft(mockEmail);
      setLoading(false);
    };

    fetchEmail();
  }, []);

  const handleConfirm = () => {
    // Simulate navigation since useNavigate isn't available
    alert("Email confirmed! Redirecting to confirmation page...");
  };

  const handleTextareaClick = () => {
    setIsEditing(true);
  };

  const handleTextareaBlur = () => {
    setIsEditing(false);
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-indigo-50 via-white to-purple-50 flex items-center justify-center p-4">
        <div className="bg-white rounded-2xl shadow-2xl p-8 max-w-md w-full text-center">
          <div className="relative mb-6">
            <div className="w-16 h-16 mx-auto bg-gradient-to-r from-indigo-500 to-purple-600 rounded-full flex items-center justify-center animate-pulse">
              <Sparkles className="w-8 h-8 text-white" />
            </div>
            <div className="absolute inset-0 w-16 h-16 mx-auto bg-gradient-to-r from-indigo-500 to-purple-600 rounded-full animate-ping opacity-20"></div>
          </div>
          <h3 className="text-xl font-semibold text-gray-800 mb-2">Generating Your Email</h3>
          <p className="text-gray-600 mb-4">AI is crafting the perfect message for you...</p>
          <div className="flex justify-center space-x-1">
            <div className="w-2 h-2 bg-indigo-500 rounded-full animate-bounce"></div>
            <div className="w-2 h-2 bg-indigo-500 rounded-full animate-bounce" style={{animationDelay: '0.1s'}}></div>
            <div className="w-2 h-2 bg-indigo-500 rounded-full animate-bounce" style={{animationDelay: '0.2s'}}></div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-50 via-white to-purple-50 p-4">
      <div className="max-w-4xl mx-auto py-8">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="inline-flex items-center justify-center w-16 h-16 bg-gradient-to-r from-indigo-500 to-purple-600 rounded-full mb-4 shadow-lg">
            <Mail className="w-8 h-8 text-white" />
          </div>
          <h1 className="text-4xl font-bold bg-gradient-to-r from-indigo-600 to-purple-600 bg-clip-text text-transparent mb-2">
            Email Preview & Editor
          </h1>
          <p className="text-gray-600 text-lg">Review and customize your AI-generated email</p>
        </div>

        {/* Main Content Card */}
        <div className="bg-white rounded-3xl shadow-2xl p-8 transform transition-all duration-500 hover:shadow-3xl">
          <div className="flex items-center justify-between mb-6">
            <div className="flex items-center space-x-3">
              <Edit3 className="w-5 h-5 text-indigo-500" />
              <h2 className="text-xl font-semibold text-gray-800">Your Email Draft</h2>
            </div>
            <div className="flex items-center space-x-2 text-sm text-gray-500">
              <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse"></div>
              <span>Ready to edit</span>
            </div>
          </div>

          {/* Email Content */}
          <div className="relative mb-8">
            <textarea
              className={`w-full p-6 border-2 rounded-2xl resize-none font-mono text-sm leading-relaxed transition-all duration-300 focus:outline-none ${
                isEditing
                  ? 'border-indigo-400 bg-indigo-50/50 shadow-lg ring-4 ring-indigo-100'
                  : 'border-gray-200 bg-gray-50 hover:border-gray-300 hover:shadow-md'
              }`}
              rows="16"
              value={emailDraft}
              onChange={(e) => setEmailDraft(e.target.value)}
              onClick={handleTextareaClick}
              onBlur={handleTextareaBlur}
              placeholder="Your email content will appear here..."
            />
            <div className="absolute top-4 right-4 opacity-60">
              <Edit3 className="w-4 h-4 text-gray-400" />
            </div>
          </div>

          {/* Action Buttons */}
          <div className="flex flex-col sm:flex-row gap-4 justify-center">
            <button
              onClick={handleConfirm}
              className="group relative px-8 py-4 bg-gradient-to-r from-indigo-500 to-purple-600 text-white font-semibold rounded-2xl shadow-lg hover:shadow-xl transform transition-all duration-300 hover:-translate-y-1 hover:scale-105 focus:outline-none focus:ring-4 focus:ring-indigo-200"
            >
              <div className="flex items-center justify-center space-x-2">
                <Send className="w-5 h-5 transition-transform duration-300 group-hover:translate-x-1" />
                <span>Confirm & Send</span>
                <ArrowRight className="w-4 h-4 transition-transform duration-300 group-hover:translate-x-1" />
              </div>
              <div className="absolute inset-0 bg-gradient-to-r from-purple-600 to-indigo-500 rounded-2xl opacity-0 transition-opacity duration-300 group-hover:opacity-100"></div>
              <div className="relative z-10 flex items-center justify-center space-x-2">
                <Send className="w-5 h-5 transition-transform duration-300 group-hover:translate-x-1" />
                <span>Confirm & Send</span>
                <ArrowRight className="w-4 h-4 transition-transform duration-300 group-hover:translate-x-1" />
              </div>
            </button>

            <button className="group px-8 py-4 bg-white border-2 border-gray-200 text-gray-700 font-semibold rounded-2xl shadow-md hover:shadow-lg transform transition-all duration-300 hover:-translate-y-1 hover:border-gray-300 focus:outline-none focus:ring-4 focus:ring-gray-100">
              <div className="flex items-center justify-center space-x-2">
                <Edit3 className="w-5 h-5 transition-transform duration-300 group-hover:rotate-12" />
                <span>Edit More</span>
              </div>
            </button>
          </div>

          {/* Tips Section */}
          <div className="mt-8 p-6 bg-gradient-to-r from-blue-50 to-indigo-50 rounded-2xl border border-blue-100">
            <div className="flex items-start space-x-3">
              <div className="flex-shrink-0 w-8 h-8 bg-blue-100 rounded-full flex items-center justify-center mt-1">
                <Sparkles className="w-4 h-4 text-blue-600" />
              </div>
              <div>
                <h3 className="font-semibold text-blue-900 mb-2">Pro Tips</h3>
                <ul className="text-sm text-blue-700 space-y-1">
                  <li>• Personalize the greeting with the hiring manager's name if known</li>
                  <li>• Highlight 2-3 key achievements relevant to the position</li>
                  <li>• Keep it concise - aim for 3-4 paragraphs maximum</li>
                  <li>• End with a clear call to action</li>
                </ul>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default EmailEditor;
