import React, { useState } from "react";
import axios from "axios";
import { Upload, FileText, Users, ArrowRight, Check, Loader } from "lucide-react";
import { useNavigate } from "react-router-dom";

function ConfirmPage() {
  const [file, setFile] = useState(null);
  const [listData, setListData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [dragOver, setDragOver] = useState(false);
  const navigate = useNavigate();

  const handleFileChange = (e) => setFile(e.target.files[0]);

  const handleDragOver = (e) => {
    e.preventDefault();
    setDragOver(true);
  };

  const handleDragLeave = (e) => {
    e.preventDefault();
    setDragOver(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setDragOver(false);
    const droppedFile = e.dataTransfer.files[0];
    if (droppedFile && droppedFile.type === "text/csv") {
      setFile(droppedFile);
    }
  };

  const handleFetchList = async () => {
    if (!file) return alert("Please select a CSV file.");

    const formData = new FormData();
    formData.append("file", file);

    try {
      setLoading(true);

      const response = await axios.post("http://localhost:8080/resume/csv-upload", formData, {
        headers: { "Content-Type": "multipart/form-data" },
      });

      // Expecting backend returns JSON list of { name, email }
      setListData(response.data);

    } catch (error) {
      console.error("Error fetching list:", error);
      alert("Failed to fetch list from backend.");
    } finally {
      setLoading(false);
    }
  };

  const handleNext = () => {
    navigate("/send-email", { state: { csvList: listData } });
  };

  // --- styles remain unchanged from your first design ---
  const styles = { /* same as your first code block */ };
  const keyframes = `/* same as your first code block */`;

  return (
    <>
      <style>{keyframes}</style>
      <div style={styles.container}>
        <div style={styles.wrapper}>
          {/* Header */}
          <div style={styles.header}>
            <div style={styles.headerIcon}>
              <FileText size={32} color="white" />
            </div>
            <h1 style={styles.title}>Contact List Manager</h1>
            <p style={styles.subtitle}>Upload your CSV file and manage your contact list</p>
          </div>

          {/* Main Content */}
          <div style={styles.mainContent} className="main-content">
            {/* Upload Section */}
            <div style={styles.uploadCard} className="upload-card">
              <h2 style={styles.uploadTitle}>
                <Upload size={24} />
                Upload CSV File
              </h2>

              <div
                style={styles.dropZone}
                onDragOver={handleDragOver}
                onDragLeave={handleDragLeave}
                onDrop={handleDrop}
                onClick={() => document.getElementById("fileInput").click()}
              >
                <Upload size={48} color={dragOver ? "#667eea" : "#cbd5e0"} />
                <p style={styles.dropZoneText}>
                  {dragOver ? "Drop your CSV file here" : "Click to upload or drag & drop your CSV file"}
                </p>
                <p style={{ ...styles.dropZoneText, fontSize: "0.8rem", marginTop: "0.5rem" }}>
                  Only CSV files are accepted
                </p>
              </div>

              <input
                id="fileInput"
                type="file"
                accept=".csv"
                onChange={handleFileChange}
                style={styles.fileInput}
              />

              {file && (
                <div style={styles.selectedFile}>
                  <Check size={20} />
                  <span>{file.name}</span>
                </div>
              )}

              <div style={styles.buttonGroup}>
                <button
                  onClick={handleFetchList}
                  disabled={!file || loading}
                  style={{
                    ...styles.button,
                    ...styles.primaryButton,
                    ...((!file || loading) && styles.disabledButton),
                  }}
                  className="button"
                >
                  {loading ? (
                    <>
                      <Loader size={18} className="spinner" style={styles.spinner} />
                      Processing...
                    </>
                  ) : (
                    <>
                      <FileText size={18} />
                      Load Contact List
                    </>
                  )}
                </button>

                <button
                  onClick={handleNext}
                  disabled={listData.length === 0}
                  style={{
                    ...styles.button,
                    ...styles.secondaryButton,
                    ...(listData.length === 0 && styles.disabledButton),
                  }}
                  className="button"
                >
                  <span>Continue to Send Emails</span>
                  <ArrowRight size={18} />
                </button>
              </div>
            </div>

            {/* Preview Section */}
            <div style={styles.previewCard} className="preview-card">
              <div style={styles.previewHeader}>
                <h3 style={styles.previewTitle}>
                  <Users size={24} />
                  Contact List Preview
                </h3>
                {listData.length > 0 && <span style={styles.contactCount}>{listData.length} contacts</span>}
              </div>

              {loading && (
                <div style={styles.loadingSpinner}>
                  <Loader size={40} style={styles.spinner} color="#667eea" />
                  <p>Loading contact list...</p>
                </div>
              )}

              {!loading && listData.length === 0 && (
                <div style={styles.emptyState}>
                  <Users size={64} color="#cbd5e0" />
                  <p style={{ marginTop: "1rem", fontSize: "1.1rem" }}>No contacts loaded yet</p>
                  <p style={{ marginTop: "0.5rem" }}>Upload a CSV file to see your contact list here</p>
                </div>
              )}

              {!loading && listData.length > 0 && (
                <div style={styles.tableContainer}>
                  <table style={styles.table}>
                    <thead style={styles.tableHeader}>
                      <tr>
                        <th style={styles.tableHeaderCell}>Name</th>
                        <th style={styles.tableHeaderCell}>Email Address</th>
                      </tr>
                    </thead>
                    <tbody>
                      {listData.map((item, index) => (
                        <tr key={index} style={styles.tableRow} className="table-row">
                          <td style={styles.tableCell}>{item.name}</td>
                          <td style={styles.tableCell}>{item.email}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default ConfirmPage;
