import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { Upload, FileText, Users, ArrowRight, Check, Loader } from "lucide-react";

function ConfirmPage() {
  const [file, setFile] = useState(null);
  const [listData, setListData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [dragOver, setDragOver] = useState(false);
  const navigate = useNavigate();

  const handleFileChange = (e) => setFile(e.target.files[0]);

  const handleDragOver = (e) => {
    e.preventDefault();
    setDragOver(true);
  };

  const handleDragLeave = (e) => {
    e.preventDefault();
    setDragOver(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setDragOver(false);
    const droppedFile = e.dataTransfer.files[0];
    if (droppedFile && droppedFile.type === "text/csv") {
      setFile(droppedFile);
    }
  };

  const handleFetchList = async () => {
    if (!file) return alert("Please select a CSV file.");

    const formData = new FormData();
    formData.append("file", file);

    try {
      setLoading(true);

      const response = await axios.post(
        "http://localhost:8080/resume/csv-upload",
        formData,
        { headers: { "Content-Type": "multipart/form-data" } }
      );

      // Assuming backend returns JSON list of {name, email}
      setListData(response.data);
    } catch (error) {
      console.error("Error fetching list:", error);
      alert("Failed to fetch list from backend.");
    } finally {
      setLoading(false);
    }
  };

  const handleNext = () => {
    navigate("/send-email", { state: { csvList: listData } });
  };

  const styles = {
    container: { padding: "2rem", fontFamily: "Inter, sans-serif" },
    uploadCard: {
      background: "#f7fafc",
      borderRadius: "1rem",
      padding: "2rem",
      border: "1px solid #e2e8f0",
      display: "flex",
      flexDirection: "column",
      gap: "1rem"
    },
    dropZone: {
      border: `2px dashed ${dragOver ? "#667eea" : "#cbd5e0"}`,
      borderRadius: "0.75rem",
      padding: "2rem",
      textAlign: "center",
      cursor: "pointer",
      background: dragOver ? "rgba(102,126,234,0.05)" : "#fff",
      transition: "all 0.3s ease"
    },
    selectedFile: {
      background: "#48bb78",
      color: "#fff",
      padding: "0.75rem",
      borderRadius: "0.5rem",
      display: "flex",
      alignItems: "center",
      gap: "0.5rem"
    },
    button: {
      padding: "0.75rem 1.5rem",
      border: "none",
      borderRadius: "0.5rem",
      fontWeight: "600",
      cursor: "pointer",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      gap: "0.5rem"
    },
    primaryButton: { background: "#667eea", color: "#fff" },
    disabledButton: { background: "#e2e8f0", color: "#a0aec0", cursor: "not-allowed" },
    table: { width: "100%", borderCollapse: "collapse" },
    tableCell: { border: "1px solid #ccc", padding: "8px" }
  };

  return (
    <div style={styles.container}>
      <div style={{ display: "flex", gap: "2rem" }}>
        {/* Upload Section */}
        <div style={{ flex: 1 }}>
          <div style={styles.uploadCard}>
            <h2 style={{ display: "flex", alignItems: "center", gap: "0.5rem" }}>
              <Upload size={24} /> Upload CSV
            </h2>

            <div
              style={styles.dropZone}
              onDragOver={handleDragOver}
              onDragLeave={handleDragLeave}
              onDrop={handleDrop}
              onClick={() => document.getElementById("fileInput").click()}
            >
              <Upload size={48} color={dragOver ? "#667eea" : "#cbd5e0"} />
              <p>{dragOver ? "Drop your CSV here" : "Click or drag & drop CSV file"}</p>
            </div>

            <input
              type="file"
              id="fileInput"
              accept=".csv"
              onChange={handleFileChange}
              style={{ display: "none" }}
            />

            {file && (
              <div style={styles.selectedFile}>
                <Check size={16} /> {file.name}
              </div>
            )}

            <button
              onClick={handleFetchList}
              disabled={!file || loading}
              style={{
                ...styles.button,
                ...styles.primaryButton,
                ...((!file || loading) && styles.disabledButton)
              }}
            >
              {loading ? (
                <>
                  <Loader size={16} /> Processing...
                </>
              ) : (
                <>
                  <FileText size={16} /> Load List
                </>
              )}
            </button>

            <button
              onClick={handleNext}
              disabled={listData.length === 0}
              style={{
                ...styles.button,
                ...styles.primaryButton,
                ...(listData.length === 0 && styles.disabledButton),
                marginTop: "0.5rem"
              }}
            >
              <ArrowRight size={16} /> Next
            </button>
          </div>
        </div>

        {/* List Preview */}
        <div style={{ flex: 2 }}>
          <h3 style={{ marginBottom: "1rem", display: "flex", alignItems: "center", gap: "0.5rem" }}>
            <Users size={24} /> Contact List Preview
          </h3>

          {loading && <p>Loading...</p>}
          {!loading && listData.length === 0 && <p>No list loaded yet.</p>}
          {!loading && listData.length > 0 && (
            <table style={styles.table}>
              <thead>
                <tr>
                  <th style={styles.tableCell}>Name</th>
                  <th style={styles.tableCell}>Email</th>
                </tr>
              </thead>
              <tbody>
                {listData.map((item, index) => (
                  <tr key={index}>
                    <td style={styles.tableCell}>{item.name}</td>
                    <td style={styles.tableCell}>{item.email}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      </div>
    </div>
  );
}

export default ConfirmPage;
