import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { Mail, Send, Sparkles, Edit3 } from "lucide-react";

export default function EmailEditor() {
  const [emailDraft, setEmailDraft] = useState("Loading email...");
  const [loading, setLoading] = useState(true);
  const [isEditing, setIsEditing] = useState(false);
  const [showConfetti, setShowConfetti] = useState(false);
  const navigate = useNavigate();

  // ‚úÖ Fetch email from backend
  useEffect(() => {
    const fetchEmail = async () => {
      try {
        const response = await axios.post(
          "http://localhost:8080/resume/generate-mail"
        );
        setEmailDraft(response.data);
      } catch (error) {
        console.error("Error fetching email:", error);
        setEmailDraft("Error fetching email from backend.");
      } finally {
        setLoading(false);
      }
    };

    fetchEmail();
  }, []);

  // ‚úÖ Update email in backend and navigate
  const handleConfirm = async () => {
    setShowConfetti(true);
    setTimeout(() => setShowConfetti(false), 3000);

    try {
      await axios.post("http://localhost:8080/resume/update-mail", emailDraft, {
        headers: { "Content-Type": "text/plain" },
      });
      navigate("/confirm", { state: { finalEmail: emailDraft } });
    } catch (error) {
      console.error("Error updating email:", error);
      alert("Failed to update email in backend!");
    }
  };

  // üñåÔ∏è All your existing styles (unchanged)
  const styles = { /* ... keep your existing styles exactly as you had ... */

  container: {
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      padding: '2rem',
