      zIndex: 0,
    },

   floatingIcon: {
  position: 'absolute',
  top: '10%',
  left: '10%',
  transform: 'translate(-50%, -50%)',
  width: '80px',
  height: '80px',
  background: 'rgba(255, 255, 255, 0.1)',
  borderRadius: '50%',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  backdropFilter: 'blur(10px)',
  border: '1px solid rgba(255, 255, 255, 0.2)',
  animation: 'float 6s ease-in-out infinite',
  zIndex: 1,
},



