import React, { useState } from "react";
import { Upload, FileText, Users, ArrowRight, Check, X, Loader } from "lucide-react";

function ConfirmPage() {
  const [file, setFile] = useState(null);
  const [listData, setListData] = useState([]);
  const [loading, setLoading] = useState(false);
  const [dragOver, setDragOver] = useState(false);

  const handleFileChange = (e) => setFile(e.target.files[0]);

  const handleDragOver = (e) => {
    e.preventDefault();
    setDragOver(true);
  };

  const handleDragLeave = (e) => {
    e.preventDefault();
    setDragOver(false);
  };

  const handleDrop = (e) => {
    e.preventDefault();
    setDragOver(false);
    const droppedFile = e.dataTransfer.files[0];
    if (droppedFile && droppedFile.type === "text/csv") {
      setFile(droppedFile);
    }
  };

  const handleFetchList = async () => {
    if (!file) return alert("Please select a CSV file.");

    try {
      setLoading(true);

      // Simulate API call with mock data
      await new Promise(resolve => setTimeout(resolve, 2000));

      const mockData = [
        { name: "John Doe", email: "john.doe@example.com" },
        { name: "Jane Smith", email: "jane.smith@company.com" },
        { name: "Michael Johnson", email: "m.johnson@tech.io" },
        { name: "Sarah Wilson", email: "sarah.w@startup.com" },
        { name: "David Brown", email: "d.brown@enterprise.org" },
        { name: "Emma Davis", email: "emma.davis@innovation.net" },
        { name: "Chris Anderson", email: "c.anderson@solutions.com" },
        { name: "Lisa Martinez", email: "lisa.m@consulting.biz" }
      ];

      setListData(mockData);

    } catch (error) {
      console.error("Error fetching list:", error);
      alert("Failed to fetch list from backend.");
    } finally {
      setLoading(false);
    }
  };

  const handleNext = () => {
    // Simulate navigation
    alert(`Proceeding with ${listData.length} contacts to send emails!`);
  };

  const styles = {
    container: {
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      padding: '2rem',
      fontFamily: 'Inter, system-ui, sans-serif'
    },
    wrapper: {
      maxWidth: '1400px',
      margin: '0 auto'
    },
    header: {
